function snakegame() 
{
	function init()
	{
		console.log("in Init Function");
    canvas=document.getElementById('mycanvas')
	w=canvas.width=1000;
    h=canvas.height=1000;
    pen=canvas.getContext('2d');
    cs=67;// cell size
    game_over=false;
    score=5;


    // Creating Object for Food Image 
    food_img=new Image();
    food_img.src="apple.png";

    trophy_img=new Image();
    trophy_img.src="trophy.png";
    food=getRandomFood();
    snake=
    {
      init_len:5,
      color:"blue",
      cell:[],
      direction:"right",

      createSnake:function()
      {
      	for(var i=this.init_len;i>0;i--)
      	{
      		this.cell.push({x:i,y:0});
      	}
      },


      drawSnake:function()
      {
      	for(var i=0;i<this.cell.length;i++)
      	{
      		pen.fillStyle=this.color;
      		pen.fillRect(this.cell[i].x*cs,this.cell[i].y*cs,cs-2,cs-2);
      	}
      },


      updatingSnake:function()
      {
      	console.log("updating snake according to the pressed key ");
      	//If snake has eatenthe food then increasing the length of the food and generrating new position of food 
      	var headX=this.cell[0].x;
       var headY=this.cell[0].y;
       if(headX==food.x && headY==food.y) // if snake and food coordinate collapse each other that means food is eaten 
       {
       	  console.log("Food Eaten");
       	  food=getRandomFood();
       	  beep();
       	  score++;
       }
       else 
       {
       	    this.cell.pop(); // pop the last cell of the snake 
       }

    
       
       var nextX,nextY;
       if(this.direction=="right")
       {
       	nextX=headX+1;
       	nextY=headY;
       }
       else if(this.direction=="left")
       {
       	nextX=headX-1;
       	nextY=headY;
       }
       else if(this.direction=="down")
       {
       	nextX=headX;
       	nextY=headY+1;
       }
       else 
       {
       	nextX=headX;
       	nextY=headY-1;
       }
       this.cell.unshift({x:nextX,y:nextY});

       // Logic that prevent snake to going outside
        last_x=Math.round(w/cs);
        last_y=Math.round(h/cs);

        if(this.cell[0].x<0 || this.cell[0].y<0 || this.cell[0].x>last_x || this.cell[0].y>last_y)
        {
        	game_over=true;
        }
      }


    };

    snake.createSnake();


    function keyPressed(e)
    {
        if(e.key=="ArrowRight")
        {
        	snake.direction="right";
        }
        else if(e.key=="ArrowLeft")
        {
        	snake.direction="left";
        }
        else if(e.key=="ArrowDown")
        {
        	snake.direction="down";
        }
        else
        {
        	snake.direction="up";
        }
    console.log(snake.direction);
    }


    document.addEventListener('keydown',keyPressed);


}
	function draw()
	{
   // console.log("In draw Function");

    pen.clearRect(0,0,w,h); // erase the old Frame 
    snake.drawSnake();

    pen.fillStyle=food.color;
    pen.drawImage(food_img,food.x*cs,food.y*cs,cs,cs);
     
    pen.drawImage(trophy_img,18,20,cs,cs);
    pen.fillStyle="blue";
    pen.font="20px Roboto"
    pen.fillText(score,50,50);
	}


	function update()
	{
   // console.log("In update Function");
   snake.updatingSnake();
	}

function getRandomFood()
{
	FoodX=Math.round(Math.random()*(w-cs)/cs);
	FoodY=Math.round(Math.random()*(h-cs)/cs);

	Food=
	{
		x:FoodX,
		y:FoodY,
		color:"red",
	}
	return Food;
}

	function gameloop()
	{
	//	console.log("In gameloop Function");
	  if(game_over==true)
	  {
	  	clearInterval(f);
	  	alert("Game Over");
	  }
       draw();
       update();
	}
function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}

	init();
	var f=setInterval(gameloop,100);
	
}